import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '/assets/logo.png';

// Use inline styles to avoid module issues
const Home = () => {
  const navigate = useNavigate();

  const handleCheckIn = () => {
    navigate('/cvcheckin');
  };

  // Styles defined inline to ensure they apply
  const styles = {
    homePage: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#1a1a1a',
      padding: '20px',
    },
    appHeader: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginBottom: '40px',
    },
    logo: {
      maxWidth: '200px',
      height: 'auto',
      marginBottom: '8px',
    },
    subtitle: {
      color: 'white',
      fontSize: '16px',
      fontWeight: '400',
      textAlign: 'center',
    },
    cardContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flex: 1,
      width: '100%',
      maxWidth: '500px',
      padding: '20px',
    },
    checkInCard: {
      backgroundColor: '#e8f0e8',
      width: '100%',
      maxWidth: '400px',
      borderRadius: '16px',
      overflow: 'hidden',
      boxShadow: '0 6px 16px rgba(0, 0, 0, 0.15)',
    },
    cardHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '16px',
    },
    cardBadge: {
      display: 'flex',
      alignItems: 'center',
    },
    checkIcon: {
      width: '24px',
      height: '24px',
      backgroundColor: '#3c7b9e',
      color: 'white',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '14px',
      marginRight: '8px',
    },
    badgeText: {
      fontWeight: '500',
      color: '#333',
      fontSize: '16px',
    },
    infoCircle: {
      width: '24px',
      height: '24px',
      backgroundColor: 'rgba(60, 123, 158, 0.15)',
      color: '#3c7b9e',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '14px',
      fontStyle: 'italic',
      fontWeight: 'bold',
      cursor: 'pointer',
    },
    cardBody: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '40px 20px',
    },
    checkInButton: {
      backgroundColor: '#3c7b9e',
      color: 'white',
      fontSize: '18px',
      fontWeight: '500',
      padding: '12px 36px',
      border: 'none',
      borderRadius: '32px',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      width: '80%',
      maxWidth: '200px',
    }
  };

  return (
    <div style={styles.homePage}>
      <div style={styles.appHeader}>
        <img src="/assets/logo.png"  alt="OptiCheck" style={styles.logo} />
      </div>

      <div style={styles.cardContainer}>
        <div style={styles.checkInCard}>
          <div style={styles.cardHeader}>
            <div style={styles.cardBadge}>
              <div style={styles.checkIcon}>âœ“</div>
              <span style={styles.badgeText}>OptiCheck</span>
            </div>
            <div style={styles.infoCircle}>i</div>
          </div>

          <div style={styles.cardBody}>
            <button 
              style={styles.checkInButton}
              onClick={handleCheckIn}
            >
              Check-In
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
