import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const [animateIn, setAnimateIn] = useState(false);

  useEffect(() => {
    // Trigger entrance animations after component mounts
    setAnimateIn(true);

    // Add Comfortaa font to document if not already present
    if (!document.getElementById('comfortaa-font')) {
      const link = document.createElement('link');
      link.id = 'comfortaa-font';
      link.rel = 'stylesheet';
      link.href = 'https://fonts.googleapis.com/css2?family=Comfortaa:wght@300;400;500;600;700&display=swap';
      document.head.appendChild(link);
    }
  }, []);

  const handleCheckIn = () => {
    navigate('/cvcheckin');
  };

  // Advanced styles with animations and modern UI elements
  const styles = {
    homePage: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a1a1a 0%, #2c3e50 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: '"Comfortaa", sans-serif',
    },
    logoContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginBottom: '30px',
      transform: animateIn ? 'translateY(0)' : 'translateY(-20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease, opacity 0.8s ease',
      width: '100%',
      maxWidth: '600px',
    },
    logo: {
      width: '150px',
      height: 'auto',
      marginBottom: '15px',
      filter: 'drop-shadow(0 0 10px rgba(111, 226, 204, 0.4))',
    },
    welcomeText: {
      color: 'white',
      fontSize: '28px',
      fontWeight: '600',
      textAlign: 'center',
      marginBottom: '8px',
      textShadow: '0 2px 4px rgba(0,0,0,0.3)',
      fontFamily: '"Comfortaa", sans-serif',
    },
    subtitle: {
      color: 'rgba(255, 255, 255, 0.7)',
      fontSize: '16px',
      fontWeight: '400',
      textAlign: 'center',
      fontFamily: '"Comfortaa", sans-serif',
    },
    cardContainer: {
      width: '100%',
      maxWidth: '600px', // Increased width
      transform: animateIn ? 'translateY(0)' : 'translateY(20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease 0.2s, opacity 0.8s ease 0.2s',
      flex: '1', // Take up available space
      display: 'flex',
      flexDirection: 'column',
    },
    checkInCard: {
      backgroundColor: '#E0F3D8', // Using the E0F3D8 color
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 10px 30px rgba(0, 0, 0, 0.25), 0 0 0 1px rgba(255, 255, 255, 0.1) inset',
      backdropFilter: 'blur(10px)',
      transform: 'perspective(1000px)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      flex: '1', // Take up available space
      display: 'flex',
      flexDirection: 'column',
    },
    cardHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '20px',
      borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
    },
    cardBadge: {
      display: 'flex',
      alignItems: 'center',
    },
    checkIconContainer: {
      width: '32px',
      height: '32px',
      backgroundColor: '#6fe2cc',
      color: '#2c3e50',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      marginRight: '10px',
      boxShadow: '0 2px 8px rgba(111, 226, 204, 0.4)',
    },
    checkIcon: {
      fontSize: '18px',
      fontWeight: 'bold',
    },
    badgeText: {
      fontWeight: '600',
      color: '#2c3e50',
      fontSize: '18px',
      fontFamily: '"Comfortaa", sans-serif',
    },
    infoCircle: {
      width: '28px',
      height: '28px',
      backgroundColor: 'rgba(111, 226, 204, 0.15)',
      color: '#2c3e50',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '16px',
      fontStyle: 'italic',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'transform 0.2s ease, background-color 0.2s ease',
    },
    cardBody: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 20px',
      flex: '1', // Take up available space
    },
    checkInButton: {
      backgroundColor: '#6fe2cc',
      color: '#2c3e50',
      fontSize: '26px', // Even larger font
      fontWeight: '600',
      padding: '20px', // Equal padding all around
      border: 'none',
      borderRadius: '50px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 12px rgba(111, 226, 204, 0.4)',
      position: 'relative',
      overflow: 'hidden',
      width: '80%', // Percentage of container width
      height: '220px', // Fixed height to make it large
      maxWidth: '350px',
      outline: 'none',
      fontFamily: '"Comfortaa", sans-serif',
    },
    particles: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      pointerEvents: 'none',
    },
    particle: {
      position: 'absolute',
      width: '8px',
      height: '8px',
      borderRadius: '50%',
      backgroundColor: 'rgba(224, 243, 216, 0.3)', // Using E0F3D8 for particles
      pointerEvents: 'none',
    }
  };

  // Create background particles for visual effect
  const particles = [];
  for (let i = 0; i < 30; i++) {
    const animationDuration = 20 + Math.random() * 20;
    const size = 5 + Math.random() * 15;
    particles.push({
      left: `${Math.random() * 100}%`,
      top: `${Math.random() * 100}%`,
      width: `${size}px`,
      height: `${size}px`,
      animationDuration: `${animationDuration}s`,
      opacity: 0.1 + Math.random() * 0.3,
      animation: `float ${animationDuration}s ease-in-out infinite`,
      transformOrigin: 'center center',
      transform: `translate(0, 0) rotate(0deg)`,
    });
  }

  return (
    <div style={styles.homePage}>
      {/* Background particles */}
      <div style={styles.particles}>
        {particles.map((particle, index) => (
          <div 
            key={index} 
            style={{
              ...styles.particle,
              ...particle,
            }} 
          />
        ))}
      </div>

      <div style={styles.logoContainer}>
        {/* Try to use the logo.png if it exists, otherwise fallback to SVG */}
        <div style={styles.logo}>
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{width: '100%', height: '100%'}}>
            <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" 
                  fill="#6fe2cc" fillOpacity="0.2" />
            <path d="M9 12L11 14L15 10" stroke="#6fe2cc" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
            <path d="M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" 
                  stroke="#6fe2cc" strokeWidth="2" />
          </svg>
        </div>
        <div style={styles.welcomeText}>Welcome to OptiCheck</div>
        <div style={styles.subtitle}>ITD 319 Sprint | Roni Kennedy</div>
      </div>

      <div style={styles.cardContainer}>
        <div style={styles.checkInCard}>
          <div style={styles.cardHeader}>
            <div style={styles.cardBadge}>
              <div style={styles.checkIconContainer}>
                <span style={styles.checkIcon}>✓</span>
              </div>
              <span style={styles.badgeText}>OptiCheck</span>
            </div>
            <div style={styles.infoCircle}>i</div>
          </div>

          <div style={styles.cardBody}>
            <button 
              style={{
                ...styles.checkInButton,
                transform: `scale(${animateIn ? 1 : 0.95})`,
                opacity: animateIn ? 1 : 0,
              }}
              onClick={handleCheckIn}
              onMouseOver={(e) => {
                e.currentTarget.style.backgroundColor = '#7ff5df';
                e.currentTarget.style.boxShadow = '0 6px 15px rgba(111, 226, 204, 0.6)';
                e.currentTarget.style.transform = 'translateY(-2px)';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.backgroundColor = '#6fe2cc';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(111, 226, 204, 0.4)';
                e.currentTarget.style.transform = 'translateY(0)';
              }}
            >
              Check-In
            </button>
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes float {
            0% {
              transform: translate(0px, 0px) rotate(0deg);
            }
            50% {
              transform: translate(20px, -20px) rotate(10deg);
            }
            100% {
              transform: translate(0px, 0px) rotate(0deg);
            }
          }

          button:focus {
            outline: none;
          }

          html, body {
            margin: 0;
            padding: 0;
            height: 100%;
            font-family: 'Comfortaa', sans-serif;
          }
        `}
      </style>
    </div>
  );
};

export default Home;
