import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logoImage from '../../assets/logo.png';
import logoIconImage from '../../assets/logo.png';

const Home = () => {
  const navigate = useNavigate();
  const [animateIn, setAnimateIn] = useState(false);
  const [particles, setParticles] = useState([]);
  const [floatingCircles, setFloatingCircles] = useState([]);
  const [glowingOrbs, setGlowingOrbs] = useState([]);

  useEffect(() => {
    // Same effect code as before...
    setAnimateIn(true);

    // Add Comfortaa font
    if (!document.getElementById('comfortaa-font')) {
      const link = document.createElement('link');
      link.id = 'comfortaa-font';
      link.rel = 'stylesheet';
      link.href = 'https://fonts.googleapis.com/css2?family=Comfortaa:wght@300;400;500;600;700&display=swap';
      document.head.appendChild(link);
    }

    // Generate particles, floating circles, and glowing orbs as before...
    // Code omitted for brevity

  }, []);

  const handleCheckIn = () => {
    navigate('/cvcheckin');
  };

  // Advanced styles with animations and modern UI elements
  const styles = {
    homePage: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a222e 0%, #2c3e50 100%)', // Darker gradient for more contrast
      padding: '20px',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: '"Comfortaa", sans-serif',
    },
    logoContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginBottom: '40px',
      transform: animateIn ? 'translateY(0)' : 'translateY(-20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease, opacity 0.8s ease',
      position: 'relative',
      zIndex: '2',
    },
    logo: {
      width: '140px',
      height: '140px',
      marginBottom: '20px',
      filter: 'drop-shadow(0 0 10px rgba(111, 226, 204, 0.4))',
      objectFit: 'contain',
    },
    welcomeText: {
      color: 'white',
      fontSize: '28px',
      fontWeight: '600',
      textAlign: 'center',
      marginBottom: '12px',
      textShadow: '0 2px 4px rgba(0,0,0,0.3)',
      fontFamily: '"Comfortaa", sans-serif',
    },
    subtitle: {
      color: 'rgba(255, 255, 255, 0.7)',
      fontSize: '16px',
      fontWeight: '400',
      textAlign: 'center',
      fontFamily: '"Comfortaa", sans-serif',
    },
    cardContainer: {
      width: '100%',
      maxWidth: '480px', // Slightly smaller for more elegant look
      transform: animateIn ? 'translateY(0)' : 'translateY(20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease 0.2s, opacity 0.8s ease 0.2s',
      position: 'relative',
      zIndex: '2',
    },
    checkInCard: {
      backgroundColor: 'rgba(226, 230, 230, 0.9)', // More gray tone matching screenshot
      borderRadius: '24px',
      overflow: 'hidden',
      boxShadow: '0 15px 35px rgba(0, 0, 0, 0.3), 0 0 0 1px rgba(255, 255, 255, 0.1) inset',
      backdropFilter: 'blur(15px)',
      WebkitBackdropFilter: 'blur(15px)',
      transform: 'perspective(1000px)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    },
    cardHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '20px 24px', // More horizontal padding
      borderBottom: '1px solid rgba(0, 0, 0, 0.03)', // More subtle divider
    },
    cardBadge: {
      display: 'flex',
      alignItems: 'center',
    },
    checkIconContainer: {
      width: '24px', // Smaller icon
      height: '24px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      marginRight: '12px',
    },
    modernCheck: {
      width: '100%',
      height: '100%',
      position: 'relative',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      filter: 'drop-shadow(0 0 3px rgba(111, 226, 204, 0.3))', // Subtle glow on logo
    },
    badgeText: {
      fontWeight: '600',
      color: '#2c3e50',
      fontSize: '18px',
      fontFamily: '"Comfortaa", sans-serif',
    },
    infoCircle: {
      width: '24px', // Smaller info icon
      height: '24px',
      backgroundColor: 'rgba(200, 220, 220, 0.4)', // Lighter, more transparent
      color: '#2c3e50',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '14px', // Smaller
      fontStyle: 'italic',
      fontWeight: 'normal', // Not bold
      cursor: 'pointer',
      transition: 'transform 0.2s ease, background-color 0.2s ease',
      boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
    },
    cardBody: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '40px 20px', // Reduced vertical padding
    },
    checkInButton: {
      backgroundColor: 'rgba(111, 226, 204, 0.9)', // More opaque and saturated
      color: '#2c3e50',
      fontSize: '20px', // Slightly smaller
      fontWeight: '600',
      padding: '14px 0', // Vertical padding only
      border: 'none',
      borderRadius: '100px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 12px rgba(111, 226, 204, 0.2), 0 0 0 1px rgba(111, 226, 204, 0.1) inset',
      position: 'relative',
      overflow: 'hidden',
      width: '80%', // Slightly narrower
      maxWidth: '250px',
      outline: 'none',
      fontFamily: '"Comfortaa", sans-serif',
      backdropFilter: 'blur(10px)',
      WebkitBackdropFilter: 'blur(10px)',
    },
    buttonGlow: {
      position: 'absolute',
      top: '0',
      left: '0',
      right: '0',
      bottom: '0',
      background: 'radial-gradient(circle at center, rgba(111, 226, 204, 0.4) 0%, rgba(111, 226, 204, 0) 70%)',
      opacity: '0',
      transition: 'opacity 0.3s ease',
      pointerEvents: 'none',
    },
    backgroundElement: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      pointerEvents: 'none',
      zIndex: '1',
    },
    // Remaining styles for particles, etc., remain the same
  };

  return (
    <div style={styles.homePage}>
      {/* Background elements remain the same */}

      <div style={styles.logoContainer}>
        <img src={logoImage} alt="OptiCheck" style={styles.logo} />
        <div style={styles.welcomeText}>Welcome to OptiCheck</div>
        <div style={styles.subtitle}>ITD 319 Sprint | Roni Kennedy</div>
      </div>

      <div style={styles.cardContainer}>
        <div style={styles.checkInCard}>
          <div style={styles.cardHeader}>
            <div style={styles.cardBadge}>
              <div style={styles.checkIconContainer}>
                <div style={styles.modernCheck}>
                  <img 
                    src={logoIconImage}
                    alt="OptiCheck" 
                    style={{
                      width: '100%',
                      height: '100%',
                      objectFit: 'contain'
                    }} 
                  />
                </div>
              </div>
              <span style={styles.badgeText}>OptiCheck</span>
            </div>
            <div style={styles.infoCircle}>i</div>
          </div>

          <div style={styles.cardBody}>
            <button 
              style={{
                ...styles.checkInButton,
                transform: `scale(${animateIn ? 1 : 0.95})`,
                opacity: animateIn ? 1 : 0,
              }}
              onClick={handleCheckIn}
              onMouseOver={(e) => {
                e.currentTarget.style.backgroundColor = 'rgba(127, 245, 223, 0.95)';
                e.currentTarget.style.boxShadow = '0 6px 15px rgba(111, 226, 204, 0.4), 0 0 0 1px rgba(111, 226, 204, 0.2) inset';
                e.currentTarget.style.transform = 'translateY(-2px)';
                e.currentTarget.querySelector('.button-glow').style.opacity = '1';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.backgroundColor = 'rgba(111, 226, 204, 0.9)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(111, 226, 204, 0.2), 0 0 0 1px rgba(111, 226, 204, 0.1) inset';
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.querySelector('.button-glow').style.opacity = '0';
              }}
            >
              Check-In
              <div className="button-glow" style={styles.buttonGlow}></div>
            </button>
          </div>
        </div>
      </div>

      {/* Style tags, keyframes etc. remain the same */}
    </div>
  );
};

export default Home;
