import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logoImage from '../../assets/logo.png'; // Import the logo correctly

const Home = () => {
  const navigate = useNavigate();
  const [animateIn, setAnimateIn] = useState(false);

  useEffect(() => {
    // Trigger entrance animations after component mounts
    setAnimateIn(true);

    // Add Comfortaa font
    if (!document.getElementById('comfortaa-font')) {
      const link = document.createElement('link');
      link.id = 'comfortaa-font';
      link.rel = 'stylesheet';
      link.href = 'https://fonts.googleapis.com/css2?family=Comfortaa:wght@300;400;500;600;700&display=swap';
      document.head.appendChild(link);
    }
  }, []);

  const handleCheckIn = () => {
    navigate('/cvcheckin');
  };

  // Advanced styles with animations and modern UI elements
  const styles = {
    homePage: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #1a1a1a 0%, #2c3e50 100%)',
      padding: '20px',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: '"Comfortaa", sans-serif', // Add Comfortaa font
    },
    logoContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      marginBottom: '40px',
      transform: animateIn ? 'translateY(0)' : 'translateY(-20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease, opacity 0.8s ease',
    },
    logo: {
      width: '140px',
      height: '140px',
      marginBottom: '20px',
      filter: 'drop-shadow(0 0 10px rgba(111, 226, 204, 0.4))',
      objectFit: 'contain',
    },
    welcomeText: {
      color: 'white',
      fontSize: '28px',
      fontWeight: '600',
      textAlign: 'center',
      marginBottom: '12px',
      textShadow: '0 2px 4px rgba(0,0,0,0.3)',
      fontFamily: '"Comfortaa", sans-serif',
    },
    subtitle: {
      color: 'rgba(255, 255, 255, 0.7)',
      fontSize: '16px',
      fontWeight: '400',
      textAlign: 'center',
      fontFamily: '"Comfortaa", sans-serif',
    },
    cardContainer: {
      width: '100%',
      maxWidth: '500px',
      transform: animateIn ? 'translateY(0)' : 'translateY(20px)',
      opacity: animateIn ? 1 : 0,
      transition: 'transform 0.8s ease 0.2s, opacity 0.8s ease 0.2s',
    },
    checkInCard: {
      backgroundColor: 'rgba(232, 240, 232, 0.95)',
      borderRadius: '20px',
      overflow: 'hidden',
      boxShadow: '0 10px 30px rgba(0, 0, 0, 0.25), 0 0 0 1px rgba(255, 255, 255, 0.1) inset',
      backdropFilter: 'blur(10px)',
      transform: 'perspective(1000px)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    },
    cardHeader: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '20px',
      borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
    },
    cardBadge: {
      display: 'flex',
      alignItems: 'center',
    },
    checkIconContainer: {
      width: '32px',
      height: '32px',
      backgroundColor: '#6fe2cc',
      color: '#2c3e50',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      marginRight: '10px',
      boxShadow: '0 2px 8px rgba(111, 226, 204, 0.4)',
    },
    checkIcon: {
      fontSize: '18px',
      fontWeight: 'bold',
      fontFamily: '"Comfortaa", sans-serif',
    },
    badgeText: {
      fontWeight: '600',
      color: '#2c3e50',
      fontSize: '18px',
      fontFamily: '"Comfortaa", sans-serif',
    },
    infoCircle: {
      width: '28px',
      height: '28px',
      backgroundColor: 'rgba(111, 226, 204, 0.15)',
      color: '#2c3e50',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '16px',
      fontStyle: 'italic',
      fontWeight: 'bold',
      cursor: 'pointer',
      transition: 'transform 0.2s ease, background-color 0.2s ease',
    },
    cardBody: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '50px 20px', // Increased padding for larger check-in area
    },
    checkInButton: {
      backgroundColor: '#6fe2cc',
      color: '#2c3e50',
      fontSize: '22px', // Larger font
      fontWeight: '600',
      padding: '18px 50px', // Larger button
      border: 'none',
      borderRadius: '50px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 12px rgba(111, 226, 204, 0.4)',
      position: 'relative',
      overflow: 'hidden',
      width: '90%', // Wider button
      maxWidth: '280px',
      outline: 'none',
      fontFamily: '"Comfortaa", sans-serif',
    },
    particles: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      pointerEvents: 'none',
    },
    particle: {
      position: 'absolute',
      width: '8px',
      height: '8px',
      borderRadius: '50%',
      backgroundColor: 'rgba(111, 226, 204, 0.2)',
      pointerEvents: 'none',
    },
    floatingCircle: {
      position: 'absolute',
      width: '80px',
      height: '80px',
      borderRadius: '50%',
      backgroundColor: 'rgba(111, 226, 204, 0.05)',
      pointerEvents: 'none',
    }
  };

  // Create background particles for visual effect - increased to 50 particles
  const particles = [];
  for (let i = 0; i < 50; i++) { // More particles (increased from 30)
    const animationDuration = 20 + Math.random() * 30; // More variation
    const size = 5 + Math.random() * 20; // Larger possible size
    particles.push({
      left: `${Math.random() * 100}%`,
      top: `${Math.random() * 100}%`,
      width: `${size}px`,
      height: `${size}px`,
      animationDuration: `${animationDuration}s`,
      opacity: 0.1 + Math.random() * 0.3,
      animation: `float ${animationDuration}s ease-in-out infinite`,
      transformOrigin: 'center center',
      transform: `translate(0, 0) rotate(0deg)`,
    });
  }

  // Create larger floating circles for additional background animation
  const floatingCircles = [];
  for (let i = 0; i < 10; i++) {
    const size = 100 + Math.random() * 200;
    const animationDuration = 40 + Math.random() * 40;
    const delay = Math.random() * 20;
    floatingCircles.push({
      left: `${Math.random() * 100}%`,
      top: `${Math.random() * 100}%`,
      width: `${size}px`,
      height: `${size}px`,
      opacity: 0.05 + Math.random() * 0.05,
      animation: `floatSlow ${animationDuration}s ease-in-out ${delay}s infinite`,
      backgroundColor: i % 2 === 0 ? 'rgba(111, 226, 204, 0.05)' : 'rgba(224, 243, 216, 0.05)',
    });
  }

  return (
    <div style={styles.homePage}>
      {/* Background particles */}
      <div style={styles.particles}>
        {particles.map((particle, index) => (
          <div 
            key={`particle-${index}`} 
            style={{
              ...styles.particle,
              ...particle,
            }} 
          />
        ))}

        {/* Large floating circles */}
        {floatingCircles.map((circle, index) => (
          <div 
            key={`circle-${index}`} 
            style={{
              ...styles.floatingCircle,
              ...circle,
            }} 
          />
        ))}
      </div>

      <div style={styles.logoContainer}>
        {/* Use the imported logo */}
        <img src={logoImage} alt="OptiCheck" style={styles.logo} />
        <div style={styles.welcomeText}>Welcome to OptiCheck</div>
        <div style={styles.subtitle}>ITD 319 Sprint | Roni Kennedy</div>
      </div>

      <div style={styles.cardContainer}>
        <div style={styles.checkInCard}>
          <div style={styles.cardHeader}>
            <div style={styles.cardBadge}>
              <div style={styles.checkIconContainer}>
                <span style={styles.checkIcon}>✓</span>
              </div>
              <span style={styles.badgeText}>OptiCheck</span>
            </div>
            <div style={styles.infoCircle}>i</div>
          </div>

          <div style={styles.cardBody}>
            <button 
              style={{
                ...styles.checkInButton,
                transform: `scale(${animateIn ? 1 : 0.95})`,
                opacity: animateIn ? 1 : 0,
              }}
              onClick={handleCheckIn}
              onMouseOver={(e) => {
                e.currentTarget.style.backgroundColor = '#7ff5df';
                e.currentTarget.style.boxShadow = '0 6px 15px rgba(111, 226, 204, 0.6)';
                e.currentTarget.style.transform = 'translateY(-2px) scale(1.03)';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.backgroundColor = '#6fe2cc';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(111, 226, 204, 0.4)';
                e.currentTarget.style.transform = 'translateY(0) scale(1)';
              }}
            >
              Check-In
            </button>
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes float {
            0% {
              transform: translate(0px, 0px) rotate(0deg);
            }
            50% {
              transform: translate(20px, -20px) rotate(10deg);
            }
            100% {
              transform: translate(0px, 0px) rotate(0deg);
            }
          }

          @keyframes floatSlow {
            0% {
              transform: translate(0px, 0px) scale(1);
              opacity: 0.03;
            }
            50% {
              transform: translate(30px, -30px) scale(1.1);
              opacity: 0.08;
            }
            100% {
              transform: translate(0px, 0px) scale(1);
              opacity: 0.03;
            }
          }

          @keyframes pulse {
            0% {
              transform: scale(1);
              opacity: 0.8;
            }
            50% {
              transform: scale(1.1);
              opacity: 1;
            }
            100% {
              transform: scale(1);
              opacity: 0.8;
            }
          }

          button:focus {
            outline: none;
          }

          body {
            font-family: 'Comfortaa', sans-serif;
          }
        `}
